// I am solving Leetcode 126. Word Ladder II (Hard), finding all shortest transformation sequences from beginWord to endWord using words from wordList.
// The topics are: Array, Backtracking, Breadth-First Search, String, Hash Table
//
#include <iostream>
#include <vector>
#include <string>
#include <unordered_set>
#include <queue>
using namespace std;

vector<vector<string>> ladders;

bool diffOne(string a, string b)
{
    int count = 0;
    for (int i = 0; i < a.size(); i++)
    {
        if (a[i] != b[i])
            count++;
    }
    if (count == 1)
        return true;
    return false;
}

void findAllLadders(string beginWord, string endWord, vector<string> &wordList) {
    vector<vector<string>> sol;
    queue<string> q;
    unordered_set<string> visited;
    vector<string> chose;
    vector<string> tempo;
    vector<vector<string>> newa;

    bool status = false; ladders.clear();
    for (int i=0;i<wordList.size();i++){
        if (wordList[i] == endWord) status = true;
    }
    if (!status) return;

    q.push(beginWord);
    visited.insert(beginWord);
    chose.push_back(beginWord);
    ladders.push_back(chose);

    int size = 0; string temp; bool stop = false; int stopIndex = -1; int level = -1; int minLength = -1;
    while (!q.empty()){
        size = q.size(); level++;
        newa.clear();
        cout<<"Level: "<<level<<", Queue Size: "<<size<<", Ladders Size: "<<ladders.size()<<endl;

        stop = false;
        bool currStatus = false;
        minLength = -1;
        for (int i=0;i<size;i++){
            temp = q.front();
            q.pop();
            chose.clear();
            int branchCount = 0;
            visited.insert(temp);
            // we should push visited to visited rather than branches

            currStatus = false;
            for (int j=0;j<wordList.size();j++){
                if (!diffOne(wordList[j], temp)) continue;
                if (wordList[j] == endWord){
                    stopIndex = j; currStatus = true; stop = true;
                    minLength = ladders[i].size() + 1;
                    break;
                }
            }
            if (!stop){
                unordered_set<string> set;
                for (int j=0;j<wordList.size();j++){
                    if (!diffOne(wordList[j], temp)) continue;
                    if (visited.count(wordList[j])) continue;
                    if (set.count(wordList[j])) continue;
                    set.insert(wordList[j]);
                    q.push(wordList[j]);
                    chose.push_back(wordList[j]);
                    branchCount++;
                }
                if (branchCount == 0) continue;
            }
            if (currStatus){
                tempo = ladders[i] ;   
                newa.push_back(tempo);
                newa[newa.size()-1].push_back(endWord);   
            }


            for (int j = 0;j < branchCount;j++){
                tempo = ladders[i] ;   
                newa.push_back(tempo);
                newa[newa.size()-1].push_back(chose[j]);        
            }
        }
        if (newa.size() != 0) ladders = newa;
        if (minLength != -1) break;
    }

    if (ladders[0].size() == 1) ladders.clear();
    if (minLength == -1) return;
    int j =0, sizel = ladders.size();
    while (j < sizel){
        if (ladders[j][minLength-1] != endWord){
            ladders.erase(ladders.begin()+j);
            sizel--;
        } else {
            j++;
        }
    }

    return;
}

void printIt(){
    cout<<"Possible Paths are: "<<endl;
    cout<<"["<<endl;
    for (int i=0;i<ladders.size();i++){
        cout<<"\t[";
        for (int j=0;j<ladders[i].size();j++){
            cout<<ladders[i][j];
            if (j != ladders[i].size()-1) cout<<", ";
        }
        cout<<"]"<<endl;
    }
    cout<<"]"<<endl;
}

